import React from 'react';
import { VerticalTimeline, VerticalTimelineElement }  from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';
import './TimeLine.css'
import { FaSchool, FaHandsHelping, FaStar, FaHtml5, FaReact, FaNodeJs, FaNode } from 'react-icons/fa';
import { MdWork } from "react-icons/md";
import { DiReact, DiDjango, DiAndroid, DiJavascript1, DiJqueryLogo, DiNodejs } from "react-icons/di";

class TimeLine extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        iconSize: 40
    };
  }
  render() {
    return (
        <div className='timeLine'>
            <h1 className='text-center mt-5 mb-5'>Experiences </h1>
            <VerticalTimeline>
                <VerticalTimelineElement
                    className="vertical-timeline-element--extracurricular"
                    contentStyle={{ background: '#2C5F80', color: '#fff' }}
                    contentArrowStyle={{ borderRight: '7px solid  #2C5F80' }}
                    date="Jan 2020 - present"
                    iconStyle={{ background: '#2C5F80', color: '#fff' }}
                    icon={<FaStar />}
                >
                    <h3 className="vertical-timeline-element-title">Cookitty</h3>
                    <h4 className="vertical-timeline-element-subtitle  mb-2">Idea Fund</h4>
                    <span className="Icons" >
                        <DiReact size={this.state.iconSize}/>
                        <FaNodeJs size={this.state.iconSize}/>
                        <FaNode size={this.state.iconSize}/>
                        <DiNodejs size={50}/>
                    </span>
                    <p  className='mt-2'>
                        Creating a recipe app that allows users to share their love for cooking. 
                    </p>
                </VerticalTimelineElement>
                <VerticalTimelineElement
                    className="vertical-timeline-element--work"
                    contentStyle={{ background: '#8D6B48', color: '#fff' }}
                    contentArrowStyle={{ borderRight: '7px solid  #8D6B48' }}
                    date="May 2019 - Aug 2020"
                    iconStyle={{ background: '#8D6B48', color: '#fff' }}
                    icon={<MdWork />}
                >
                    <h3 className="vertical-timeline-element-title">SciLit MOOC Project</h3>
                    <h4 className="vertical-timeline-element-subtitle mb-2">SciLIFT University of Alberta</h4>
                    <span className="Icons" >
                        <DiDjango size={this.state.iconSize}/>
                        <FaHtml5 size={this.state.iconSize}/>
                        <DiJavascript1 size={this.state.iconSize}/> 
                        <DiJqueryLogo size={this.state.iconSize}/>
                    </span>
                    <p className='mt-2'>
                        Internship with the University of Albertaâ€™s Scientific Literacy MOOC Program. 
                        Responsible for designing and developing a web application allowing instructors 
                        to create sorting web pages for students to reinforce their knowledge in Scientific Literacy.
                    </p>
                </VerticalTimelineElement>
                <VerticalTimelineElement
                    className="vertical-timeline-element--work"
                    contentStyle={{ background: '#4F807B', color: '#fff' }}
                    contentArrowStyle={{ borderRight: '7px solid  #4F807B' }}
                    date="Jan 2019 - Apr 2019"
                    iconStyle={{ background: '#4F807B', color: '#fff' }}
                    icon={<FaSchool />}
                >
                    <h3 className="vertical-timeline-element-title">Walk and Talk</h3>
                    <h4 className="vertical-timeline-element-subtitle mb-2">CMPUT 401 - Software Process and Product</h4>
                    <span className="Icons" >
                        <FaReact size={this.state.iconSize}/>
                        <FaNodeJs size={this.state.iconSize}/>
                    </span>
                    <p className='mt-2'>
                    User Experience, Visual Design
                    </p>
                </VerticalTimelineElement>
                <VerticalTimelineElement
                    className="vertical-timeline-element--work"
                    contentStyle={{ background: '#4F807B', color: '#fff' }}
                    contentArrowStyle={{ borderRight: '7px solid  #4F807B' }}
                    date="Jan 2018 - Apr 2018"
                    iconStyle={{ background: '#4F807B', color: '#fff' }}
                    icon={<FaSchool />}
                >
                    <h3 className="vertical-timeline-element-title">Funky Task</h3>
                    <h4 className="vertical-timeline-element-subtitle mb-2">CMPUT 301 - Introduction to Software Engineering</h4>
                    <span className="Icons" >
                        <DiAndroid size={this.state.iconSize}/>
                    </span>
                    <p  className='mt-2'>
                    User Experience, Visual Design
                    </p>
                </VerticalTimelineElement>
                <VerticalTimelineElement
                    className="vertical-timeline-element--education"
                    contentStyle={{ background: '#BA9B98', color: '#fff' }}
                    contentArrowStyle={{ borderRight: '7px solid  #BA9B98' }}
                    date="Aug 2017 - Apr 2019"
                    iconStyle={{ background: '#BA9B98', color: '#fff' }}
                    icon={<FaHandsHelping />}
                >
                    <h3 className="vertical-timeline-element-title">Senior Peer</h3>
                    <h4 className="vertical-timeline-element-subtitle">International Student Services, UofA</h4>
                    <p>
                    Strategy, Social Media
                    </p>
                </VerticalTimelineElement>
                <VerticalTimelineElement
                    className="vertical-timeline-element--education"
                    contentStyle={{ background: '#BA9B98', color: '#fff' }}
                    contentArrowStyle={{ borderRight: '7px solid  #BA9B98' }}
                    date="Apr 2017 - Sept 2017"
                    iconStyle={{ background: '#BA9B98', color: '#fff' }}
                    icon={<FaHandsHelping />}
                >
                    <h3 className="vertical-timeline-element-title">Faculty Leader</h3>
                    <h4 className="vertical-timeline-element-subtitle">Week of Welcome, UofA</h4>
                    <p>
                    Creative Direction, User Experience, Visual Design
                    </p>
                </VerticalTimelineElement>
                <VerticalTimelineElement
                    className="vertical-timeline-element--volunteer"
                    contentStyle={{ background: '#BA9B98', color: '#fff' }}
                    contentArrowStyle={{ borderRight: '7px solid  #BA9B98' }}
                    date="Jan 2017 - Apr 2017"
                    iconStyle={{ background: '#BA9B98', color: '#fff' }}
                    icon={<FaHandsHelping />}
                >
                    <h3 className="vertical-timeline-element-title">Girl's Coding Club Mentor</h3>
                    <h4 className="vertical-timeline-element-subtitle">DiscoverE, UofA</h4>
                    <p>
                    Creative Direction, Visual Design
                    </p>
                </VerticalTimelineElement>

            </VerticalTimeline>
        </div>
    );

    }
}

export default TimeLine;
